func TestSpellCheck(t *testing.T) {
	spellErrors, _ := SpellCheckAPI(DefaultRapidAPIToken, []string{"hello", "friend", "qq", "haha", "ariba", "integration"})
	assert.DeepEqual(t, spellErrors, []string{"qq", "haha", "ariba"})
}

